        },
        "$:/plugins/twpub/1f83596b9618251c/text/000000592": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000000592",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">weather rain snow wind cloud</p></div>"
        },
        "$:/plugins/twpub/1f83596b9618251c/text/000000593": {
