            "title": "$:/plugins/twpub/1f83596b9618251c/text/000000593",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">自然类</p></div>"
        },
        "$:/plugins/twpub/1f83596b9618251c/text/000000594": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000000594",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">nature mountain air light water fire ice smoke heat ground sky river field forest sea stone star</p></div>"
        },
        "$:/plugins/twpub/1f83596b9618251c/text/000000595": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000000595",
            "type": "text/vnd.twpub",
