        },
        "$:/plugins/twpub/1f83596b9618251c/text/000000598": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000000598",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">animal bird cat dog horse rabbit elephant bear tiger lion bull cow pig chicken fish sheep monkey snake reptile</p></div>"
        },
        "$:/plugins/twpub/1f83596b9618251c/text/000000599": {
