        },
        "$:/plugins/twpub/1f83596b9618251c/text/000000602": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000000602",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">bacteria</p></div>"
        },
        "$:/plugins/twpub/1f83596b9618251c/text/000000603": {
            "role": "text",
