            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">My girlfriend gave me a kiss.</p></div>"
        },
        "$:/plugins/twpub/1f83596b9618251c/text/000001860": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000001860",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">女友给了我一个吻。</p></div>"
        },
        "$:/plugins/twpub/1f83596b9618251c/text/000001861": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000001861",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">I got a kiss from my girlfriend.</p></div>"
        },
