        },
        "$:/plugins/twpub/1f83596b9618251c/text/000002023": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000002023",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">enjoy a high standard of living　享受高标准的生活</p></div>"
        },
        "$:/plugins/twpub/1f83596b9618251c/text/000002024": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000002024",
            "type": "text/vnd.twpub",
