        "$:/plugins/twpub/1f83596b9618251c/text/000002367": {
            "role": "text",
            "title": "$:/plugins/twpub/1f83596b9618251c/text/000002367",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1f83596b9618251c-a62746 twpub-1f83596b9618251c-9882b5\"><p class=\"bodycontent-text\">flat shoes　平底鞋</p></div>"
