        },
        "$:/plugins/twpub/492fad9ebad71b30/text/000000069": {
            "role": "text",
            "title": "$:/plugins/twpub/492fad9ebad71b30/text/000000069",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-492fad9ebad71b30-0feac4 twpub-492fad9ebad71b30-9882b5\"><div class=\"calibre1\"><p class=\"right\">阳湖管贻萼树荃。    </p></div></div>"
        },
