        },
        "$:/plugins/twpub/492fad9ebad71b30/text/000000116": {
            "role": "text",
            "title": "$:/plugins/twpub/492fad9ebad71b30/text/000000116",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-492fad9ebad71b30-0feac4 twpub-492fad9ebad71b30-9882b5\"><div class=\"calibre1\">\n\n  </div></div>"
